import React, { Component } from 'react';
import Salad from './Salad';
import { Dropdown, Menu, Input } from 'semantic-ui-react'
import { Route, Link } from "react-router-dom";

class ComposeSalad extends Component {
    constructor(props) {
        super(props);
        this.state = {
            foundations : [], 
            proteins :[] ,
            extras  : [], 
            dressings : [] ,
            salads : [], 
            salad : new Salad()
        };
        this.handleChangeFoundation = this.handleChangeFoundation.bind(this);
        this.handleChangeProtein = this.handleChangeProtein.bind(this);
        this.handleChangeExtra = this.handleChangeExtra.bind(this);
        this.handleChangeDressing = this.handleChangeDressing.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleChangeFoundation(event) {
        const found = []
        if(foundations===undefined){
            found.push(event.target.value);
        }
        this.setState({
            foundations:found
        });

      }
    handleChangeProtein(event) {
        let list = [...this.state.proteins];
        if(event.target.checked){
            list.push(event.target.value);
        }
        else{
            list = list.filter(
                name => (name !== event.target.value)
            ); 
        }
        this.setState({
          proteins: list
        });
      }
    handleChangeExtra(event) {
        let list = [];
        if(event.target.checked){
            list.push(event.target.value);
        }
        else{
            list = list.filter(
                name => (name !== event.target.value)
            ); 
        }
        this.setState({
          extras: list
        });
      }
    handleChangeDressing(event) {
        this.setState(state => ({
          dressings: event.target.value
        }));
      }
    handleSubmit(){
        this.buildSalad();
        this.props.history.push('/view-order');
        alert('hello');
    }
    buildSalad(){
        this.state.salad.addFoundation(this.state.foundations);
        this.proteins.map(item => this.state.salad.addProtein(item));
        this.extras.map(item => this.state.salad.addExtras(item));
        this.dressings.map(item => this.state.salad.addDressing(item));
        this.salad.price();
    }

    render() {
        const inventory = this.props.inventory;
        let foundations = Object.keys(inventory).filter(
            name => (inventory[name].foundation)
        );
        let protein = Object.keys(inventory).filter(
            name1 => inventory[name1].protein
        );
        let extra = Object.keys(inventory).filter(
            name2 => inventory[name2].extra
        );
        let dressing = Object.keys(inventory).filter(
            name3 => inventory[name3].dressing
        );
        return (
            <div className="container">
                <React.Fragment>
                <form 
                value={this.state.value}

                >
                    <select name={this.state.name}  onChange = {this.handleChangeFoundation}>
                    {foundations.map((name) =><option value={name}>  {name} +{inventory[name].price} kr</option> )}
                    </select>
                    </form>

                </React.Fragment>
                <React.Fragment>
                <h2>Välj protein</h2>
                <form action="#">
                <p>
                {protein.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name1}
                    name={name1}
                    value = {this.state.value}
                    onChange = {this.handleChangeProtein}

                />
                <li key={name1}>{name1} + {inventory[name1].price} kr </li> </label> </ul>)}
                </p>
                </form>
                <h3>Välj extra</h3>
                <form action="#">
                <p>
                {extra.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name1}
                    name={name1}
                    value = {this.state.value}
                    onChange = {this.handleChangeProtein}
                />
                <li key={name1}>{name1} + {inventory[name1].price} kr</li> </label> </ul>)}
                </p>
                </form>
                <h4>Välj dressing</h4>
                <form action="#">
                <p>
                {dressing.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    value = {this.state.value}
                    onChange = {this.handleChangeProtein}
                />
                <li key={name1}>{name1} + {inventory[name1].price} kr </li> </label> </ul>)}
                </p>
                </form>

                </React.Fragment>
                <div type="button"
                  className="btn btn-secondary"
                  data-dismiss="modal" 
                  onClick={this.handleSubmit} >
                        Lägg till order
                </div>  
            </div>

        );
    }
}
export default ComposeSalad;
