import React, { Component } from 'react';
import Salad from './Salad';
import { Dropdown, Menu, Input } from 'semantic-ui-react'
import { Route, Link } from "react-router-dom";
import ViewOrder from './ViewOrder';

class ComposeSalad extends Component {
    constructor(props) {
        super(props);
        this.state = {
            foundations : [], 
            proteins :[] ,
            extras  : [], 
            dressings : [] ,
            salad: new Salad()
        };
        this.handleChangeFoundation = this.handleChangeFoundation.bind(this);
        this.handleChangeProtein = this.handleChangeProtein.bind(this);
        this.handleChangeExtra = this.handleChangeExtra.bind(this);
        this.handleChangeDressing = this.handleChangeDressing.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleChangeFoundation(event){
        this.setState({
            foundations:event.target.value
        });
      }
    handleChangeProtein(event) {
        let list = [...this.state.proteins];
        if (!event.target.checked){
            list = list.filter(name => (name !== event.target.value));
           } else {
            list.push(event.target.value);
           }
           this.setState({
               proteins: list
            });
        }
    handleChangeExtra(event) {
        let list = [...this.state.extras];
        if (!event.target.checked){
            list = list.filter(name => (name !== event.target.value));
           } else {
            list.push(event.target.value);
           }
           this.setState({
               extras: list
            });
        this.setState({
          extras: list
        });
      }
    handleChangeDressing(event) {
        this.setState(
        {
          dressings: event.target.value
        });
      }
    buildSalad(){
        this.state.salad.addFoundation(this.state.foundations);
        this.state.proteins.map(item => this.state.salad.addProtein(item));
        this.state.extras.map(item => this.state.salad.addExtras(item));
        this.state.salad.addDressing(this.state.dressings);
        //this.salads.push(this.state.salad);
    }
    handleSubmit(event){
        this.buildSalad();
        //this.props.addOrder(this.state.foundations);
        //Sending salad object to the Parent i.e. APP
        this.props.parentCallBack(this.state.salad);
        alert(Object.values(this.state.salad));
        event.preventDefault();

    }

    clearSalad(){
        this.state = {
            foundations : [], 
            proteins :[] ,
            extras  : [], 
            dressings : [] ,
            salads : []
        };
    }

    render() {
        const inventory = this.props.inventory;
        let foundations = Object.keys(inventory).filter(
            name => (inventory[name].foundation)
        );
        let protein = Object.keys(inventory).filter(
            name => inventory[name].protein
        );
        let extra = Object.keys(inventory).filter(
            name => inventory[name].extra
        );
        let dressing = Object.keys(inventory).filter(
            name => inventory[name].dressing
        );
        return (
            <div className="container">
                <React.Fragment>
                <form 
                value={this.state.value}
                onSubmit= {this.handleSubmit}
                >
                    <select name={this.state.name}  onChange = {this.handleChangeFoundation}>
                    {foundations.map((name) =><option value={name}>  {name} +{inventory[name].price} kr</option> )}
                    </select>
                    </form>

                <h2>Välj protein</h2>
                <form action="#">
                <p>
                {protein.map(name => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    name={this.state.name}
                    value = {name}
                    onChange = {this.handleChangeProtein}

                />
                <li key={name}>{name} + {inventory[name].price} kr </li> </label> </ul>)}
                </p>
                </form>
                <h3>Välj extra</h3>
                <form action="#">
                <p>
                {extra.map(name => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name}
                    name={this.state.name}
                    value = {name}
                    onChange = {this.handleChangeExtra}
                />
                <li key={name}>{name} + {inventory[name].price} kr</li> </label> </ul>)}
                </p>
                </form>
                <h4>Välj dressing</h4>
                <form action="#">
                <p>
                {dressing.map(name => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    name = {this.state.name}
                    value = {name}
                    onChange = {this.handleChangeDressing}
                />
                <li key={name}>{name} + {inventory[name].price} kr </li> </label> </ul>)}
                </p>
                </form>
                </React.Fragment>
                <div type="button"
                  className="btn btn-secondary"
                  data-dismiss="modal"
                  onClick={this.handleSubmit} 
                   >
                        Lägg till order
                </div>  
            </div>

        );
    }
}
export default ComposeSalad;
