import React, { Component } from 'react';
import { Dropdown, Menu, Input } from 'semantic-ui-react'
class ComposeSalad extends Component {
    constructor(props) {
        super(props);
        this.state = {
        foundation : "",
        protein : "",
        extra : "",
        dressing: ""
        };
    }

    render() {
        const inventory = this.props.inventory;
        let foundations = Object.keys(inventory).filter(
            name => (inventory[name].foundation)
        );
        let protein = Object.keys(inventory).filter(
            name1 => inventory[name1].protein
        );
        let extra = Object.keys(inventory).filter(
            name2 => inventory[name2].extra
        );
        let dressing = Object.keys(inventory).filter(
            name3 => inventory[name3].dressing
        );
        return (
            <div className="container">
                <React.Fragment>

                <select  name="foundation" required onChange={this.handleSelect}>
                <option disabled defaultValue hidden>Välj en bas</option>
                {foundations.map((name) =><option class="dropdown-item" href="#"><li  key={name}>{name} + {inventory[name].price}</li> </option>)}
             </select>
                </React.Fragment>
                <React.Fragment>
                <h2>Välj protein</h2>
                <form action="#">
                <p>
                {protein.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name1}
                    name={name1}
                />
                <span><li key={name1}>{name1} + {inventory[name1].price}</li> </span> </label> </ul>)}
                </p>
                </form>

                <h3>Välj extra</h3>
                <form action="#">
                <p>
                {extra.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name1}
                    name={name1}
                />
                <span><li key={name1}>{name1} + {inventory[name1].price}</li> </span> </label> </ul>)}
                </p>
                </form>
                <h4>Välj dressing</h4>
                <form action="#">
                <p>
                {dressing.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name1}
                    name={name1}
                />
                <span><li key={name1}>{name1} + {inventory[name1].price}</li> </span> </label> </ul>)}
                </p>
                </form>
                </React.Fragment>

            </div>

        );
    }
}
export default ComposeSalad;
