import React, { Component } from 'react'; 
import { Label } from 'semantic-ui-react';
import inventory from './inventory.ES6';

class ViewOrder extends Component {
  constructor(props) {
    super(props);
  }

    render() {
      const inventory = this.props.inventory;
      let salad = (this.props.order);
      let foundation = Object.entries(salad).filter(
        name=> name[0] ===('foundation') 
      );
      let protein = Object.entries(salad).filter(
        name=> name[0] ===('protein')
      );
      let extra = Object.entries(salad).filter(
        name=> name[0] ===('extra')
      );
      let dressing = Object.entries(salad).filter(
        name=> name[0] ===('dressing')
      );
      return (
      <div className="container">
      <table class="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Foundation</th>
              <th scope="col">Protein</th>
              <th scope="col">Dressing</th>
              <th scope="col">Extras</th>
            </tr>
          </thead>
          <tbody>

          {Object.keys(salad).map((item,id) => <tr> <th scope="row"> {id}</th> <td>{item[1] + " , "}</td> 
          {protein.map((item,id) => <td>{item[1] + " , "}</td>)}</tr>)}

          {extra.map((item,id) => <tr> <th scope="row"> {id}</th> <td>{item[1] + " , "}</td> </tr>)}
          {dressing.map((item,id) => <tr> <th scope="row"> {id}</th> <td>{item[1] + " , "}</td> </tr>)}
          </tbody>
      </table>
      <button className="btn btn-primary" >Skapa  best√§llning</button>
      </div>
      )
    }
}

export default ViewOrder;
