import React, { Component } from 'react';
import { Dropdown, Menu, Input } from 'semantic-ui-react'
class ComposeSalad extends Component {
    constructor(props) {
        super(props);
        this.state = {
            foundation : '', 
            protein : '',
            extra  : '', 
            dressing : '', 
            salad : ''
        };
        this.handleChangeFoundation = this.handleChangeFoundation.bind(this);
        this.handleChangeProtein = this.handleChangeProtein.bind(this);
        this.handleChangeExtra = this.handleChangeExtra.bind(this);
        this.handleChangeDressing = this.handleChangeDressing.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChangeFoundation() {
        this.setState(state => ({
          isToggleOn: !state.isToggleOn
        }));
      }
    handleChangeProtein(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
          [name]: value
        });
      }
    handleChangeExtra() {
        this.setState(state => ({
          isToggleOn: !state.isToggleOn
        }));
      }
    handleChangeDressing() {
        this.setState(state => ({
          isToggleOn: !state.isToggleOn
        }));
      }
    handleSubmit(){

    }

    render() {
        const inventory = this.props.inventory;
        let foundations = Object.keys(inventory).filter(
            name => (inventory[name].foundation)
        );
        let protein = Object.keys(inventory).filter(
            name1 => inventory[name1].protein
        );
        let extra = Object.keys(inventory).filter(
            name2 => inventory[name2].extra
        );
        let dressing = Object.keys(inventory).filter(
            name3 => inventory[name3].dressing
        );
        return (
            <div className="container">
                <React.Fragment>
                <form onSubmit={this.handleChangeFoundation}>
                    <select name="foundation">
                    {foundations.map((name) =><option value={name}>  {name} + {inventory[name].price}</option> )}
                    </select>
                    </form>

                </React.Fragment>
                <React.Fragment>
                <h2>Välj protein</h2>
                <form action="#">
                <p>
                {protein.map(name1 => <ul><label>
                <input name={name1}
            type="checkbox"
            checked={this.state.isGoing}
            onChange={this.handleChangeProtein}
                />
                <li key={name1}>{name1} + {inventory[name1].price}</li></label> </ul>)}
                </p>
                </form>

                <h3>Välj extra</h3>
                <form action="#">
                <p>
                {extra.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name1}
                    name={name1}
                />
                <li key={name1}>{name1} + {inventory[name1].price}</li> </label> </ul>)}
                </p>
                </form>
                <h4>Välj dressing</h4>
                <form action="#">
                <p>
                {dressing.map(name1 => <ul><label>
                <input type="checkbox"
                    class="form-check-input"
                    id={name1}
                    name={name1}
                />
                <li key={name1}>{name1} + {inventory[name1].price}</li> </label> </ul>)}
                </p>
                </form>

                </React.Fragment>

            </div>

        );
    }
}
export default ComposeSalad;
