import React from 'react';
import logo from './logo.svg';
import './App.css';
import inventory from './inventory.ES6';
import ComposeSalad from './ComposeSalad';
import ComposeSaladModal from './ComposeSaladModal';
import ViewOrder from './ViewOrder';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        saladOrders : []
    };
    this.addOrder= this.addOrder.bind(this);
  }
  addOrder(salad) {
    this.setState({
      saladOrders: this.state.saladOrders.concat(salad)
    });
  }
  render() {
    return (
    <div>
      <div className="jumbotron text-center">
        <h1>My Own Salad Bar</h1>
        <p>Here you can order custom made salads!</p> 
       </div>
      <div>
      <ComposeSaladModal inventory={inventory}/>
      <ComposeSalad inventory={inventory} parentCallback={this.addOrder}/>
      <ViewOrder order={this.state.saladOrders}/>

    </div>
  </div>
    );
}
}

export default App;
